mapper.select:
/*Ecoupon.selectOnlineCouponGbnNm*/
SELECT CMM.CMN_COM_NM 
FROM T_CPN_MST CPN 
JOIN T_CMM_COM CMM ON (CPN.CPN_MST_GBN = CMM.CMN_COM_ETC1)
WHERE CMM.CMN_COM_UP_IDX = #{CODE} AND CPN.CPN_MST_IDX = #{CPN_MST_IDX}

mapper.insert:
        /* Ecoupon.insertProduct */
        INSERT INTO T_CPN_TGT (
	   		CPN_TGT_IDX, CPN_MST_IDX, CPN_TGT_GBN, CPN_TGT_CD
		)VALUES (
			SEQ_CPN_TGT_IDX.NEXTVAL, #{CPN_MST_IDX}, #{CPN_MST_TGT_GBN} ,#{PRD_PRD_MST_CD}
		)

        /* Ecoupon.insertProductEx */
        INSERT INTO T_CPN_ETC (
	   		CPN_ETC_IDX, CPN_MST_IDX, CPN_ETC_GBN, CPN_ETC_CD
		)VALUES (
			SEQ_CPN_TGT_IDX.NEXTVAL, #{CPN_MST_IDX}, #{CPN_MST_ETC_GBN} ,#{PRD_PRD_MST_CD}
		)


        /* Ecoupon.insertOfflineCoupon */
        DECLARE
			CNT NUMBER := 0;
		BEGIN
			LOOP
				EXIT WHEN CNT = #{CPN_MST_ISU_CNT};
				CNT := CNT + 1;
	        INSERT INTO T_CPN_PTN_SHP
	            (
	                CPN_PTN_SHP_NO,
					CPN_MST_IDX,
					CPN_PTN_SHP_GBN,
					CPN_PTN_SHP_USE_YN
				)
			VALUES(
				DBMS_RANDOM.STRING('X',10) || TO_CHAR(SEQ_CPN_PTN_SHP_NO.nextval, 'FM000000'),
				#{CPN_MST_IDX},
				#{CPN_PTN_SHP_GBN},
				'N'
			);
			END LOOP;
		END;



		    SELECT CPN_MST_IDX FROM T_CPN_PTN_SHP
		    WHERE CPN_PTN_SHP_NO = #{CPN_PTN_SHP_NO}


    	/* Ecoupon.registOfflineCoupon */
    	BEGIN
    		/* 페이퍼 쿠폰 등록 처리(중복 등록 방지) */
	    	UPDATE T_CPN_PTN_SHP
	    	SET 
	    		CPN_PTN_SHP_USE_YN = 'Y',
	    		CPN_PTN_SHP_REG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'),
	    		CPN_PTN_SHP_REG_ID = #{CPN_PTN_SHP_REG_ID},
	    		CPN_PTN_SHP_REG_IP = #{CPN_PTN_SHP_REG_IP}
	    	WHERE
	    		CPN_PTN_SHP_NO = #{CPN_PTN_SHP_NO};

	    	/* 쿠폰 발행 */
	    	INSERT INTO T_CPN_MEM(
		    	CPN_MEM_IDX, CPN_MST_IDX, CPN_MEM_ID, CPN_MEM_ISU_DT, CPN_MEM_USE_DT, CPN_MEM_USE_YN

		   		, CPN_MEM_ISU_CNL

			)VALUES (
				SEQ_CPN_MEM_IDX.NEXTVAL, #{CPN_MST_IDX}, #{CPN_PTN_SHP_REG_ID} ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'), NULL, 'N'

				, #{CPN_MEM_ISU_CNL}

			);
	    END;

mapper.select:

/* Ecoupon.checkOfflineCoupon */
SELECT CPN_PTN_SHP_USE_YN, CPN_PTN_SHP_REG_ID
FROM T_CPN_PTN_SHP
WHERE
	CPN_PTN_SHP_NO = #{CPN_PTN_SHP_NO}

mapper.update:
/* Ecoupon.updateOnlineCouponStatus */
UPDATE
    T_CPN_MST CPN 
SET            
    CPN_MST_YN = #{CPN_MST_YN},   
    CPN_MST_UPD_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'),            
    CPN_MST_UPD_IP = #{CPN_MST_UPD_IP},
    CPN_MST_UPD_ID = #{CPN_MST_UPD_ID}
WHERE
    CPN_MST_IDX = #{CPN_MST_IDX}

mapper.select:
/*Ecoupon.onlineCouponIssueDetList*/

WHERE
    S2.RNUM BETWEEN #{startNum} AND #{endNum}

/* PcMyPage.onlineCouponIssueDetDwnList */ SELECT CPN_MEM_ID , MEM_MST_MEM_NM , (SELECT MEM_GRD_NM FROM T_MEM_GRD WHERE MEM_GRD_CD = MEM_MST_MEM_GRD) AS MEM_GRD , NVL(ORD_CNT, 0) AS ORD_CNT , NVL(REV_CNT, 0) AS REV_CNT , CPN_MEM_ISU_DT, CASE CPN_MEM_ISU_CNL WHEN 'P' THEN 'PC' WHEN 'A' THEN 'MOBILE APP' WHEN 'M' THEN 'MOBILE WEB' WHEN 'S' THEN '관리자 발급' ELSE '자동발급' END AS CPN_MEM_ISU_CNL FROM ( SELECT ID, COUNT(1) AS ORD_CNT FROM ( SELECT T1.* FROM ( SELECT ORD_MST_ORD_ID ID
, ORD_MST_CD , ORD_MST_ORD_DT
, ROW_NUMBER() OVER(PARTITION BY ORD_MST_CD ORDER BY ORD_ORD_EDI_ORD_NO) as PRD_RNUM , COUNT(1) OVER(PARTITION BY ORD_MST_CD) as ORD_PRD_CNT , ORD_PRD_MST_CD , ORD_ORD_EDI_ORD_NO FROM T_ORD_MST TOM, T_ORD_PRD TOP WHERE TOM.ORD_MST_CD = TOP.ORD_ORD_MST_CD AND ORD_PRD_SLT_YN = 'N'
AND ORD_PRD_ORD_STATE NOT IN
#{OST_SWAP_IGNORE}
) T1 WHERE PRD_RNUM = 1 ) T2 WHERE ID IN
#{MEM_IDX}
Group BY ID ) T3
RIGHT JOIN ( SELECT CPN_MEM_ID , MEM_MST_MEM_NM , CPN_MEM_ISU_DT , CPN_MEM_ISU_CNL , MEM_MST_MEM_GRD , REV_CNT FROM ( SELECT CPN_MEM_ID , CPN_MEM_ISU_DT , CPN_MEM_ISU_CNL FROM T_CPN_MEM WHERE CPN_MST_IDX = #{CPN_MST_IDX} AND CPN_MEM_ID IN #{MEM_IDX} GROUP BY CPN_MEM_ID ,CPN_MEM_ISU_DT ,CPN_MEM_ISU_CNL ) CM JOIN (SELECT MEM_MST_MEM_NM, MEM_MST_MEM_ID, MEM_MST_MEM_GRD FROM T_MEM_MST WHERE MEM_MST_MEM_ID IN #{MEM_IDX} ) MEM ON(CM.CPN_MEM_ID = MEM.MEM_MST_MEM_ID) LEFT JOIN (SELECT PRD_REV_REG_ID, COUNT(PRD_REV_REG_ID) AS REV_CNT FROM T_PRD_REV WHERE PRD_REV_DEL_YN = 'N' AND PRD_REV_USE_YN = 'Y' AND PRD_REV_REG_ID IN #{MEM_IDX} GROUP BY PRD_REV_REG_ID ) REV ON(CM.CPN_MEM_ID = REV.PRD_REV_REG_ID) ) ON (T3.id = CPN_MEM_ID)

    	/* Ecoupon.issueUploadCheck */
    	SELECT MEM_MST_MEM_ID
		FROM T_MEM_MST
		WHERE MEM_MST_MEM_ID IN(
		    #{MEM_ID}
		)AND MEM_MST_MEM_NM IN(
			#{MEM_NM}
		)

/* Ecoupon.onlineCouponListEditY */ SELECT ROWNUM RNUM, IV1.*, ( SELECT SUBSTR(SYS_CONNECT_BY_PATH(PRD_CTG_NM, '>'),2) FROM T_PRD_CTG WHERE PRD_CTG_IDX = DSP_PRD_CTG_IDX START WITH PRD_CTG_UP_IDX = -1 CONNECT BY PRIOR PRD_CTG_IDX = PRD_CTG_UP_IDX ) AS PRD_CTG_LVL_NM FROM (
AND PM.PRD_MST_CD IN (SELECT CPN.CPN_TGT_CD FROM T_CPN_TGT CPN WHERE CPN.CPN_TGT_GBN = #{CPN_MST_TGT_GBN} AND CPN.CPN_MST_IDX = #{CPN_MST_IDX} )
ORDER BY PRD_MST_REG_DT DESC, PM.PRD_MST_CD ASC ) IV1 ORDER BY RNUM

/* Ecoupon.onlineCouponListEditN */ SELECT ROWNUM RNUM, IV1.*, ( SELECT SUBSTR(SYS_CONNECT_BY_PATH(PRD_CTG_NM, '>'),2) FROM T_PRD_CTG WHERE PRD_CTG_IDX = DSP_PRD_CTG_IDX START WITH PRD_CTG_UP_IDX = -1 CONNECT BY PRIOR PRD_CTG_IDX = PRD_CTG_UP_IDX ) AS PRD_CTG_LVL_NM FROM (
AND PM.PRD_MST_CD IN (SELECT CPN.CPN_ETC_CD FROM T_CPN_ETC CPN WHERE CPN.CPN_ETC_GBN = #{CPN_MST_ETC_GBN} AND CPN.CPN_MST_IDX = #{CPN_MST_IDX} )
ORDER BY PRD_MST_REG_DT DESC, PM.PRD_MST_CD ASC ) IV1 ORDER BY RNUM

mapper.insert:
        /* Ecoupon.inserIssue */
        INSERT INTO T_CPN_MEM (
	   		CPN_MEM_IDX, CPN_MST_IDX, CPN_MEM_ID, CPN_MEM_ISU_DT, CPN_MEM_USE_DT, CPN_MEM_USE_YN

	   		, CPN_MEM_ISU_CNL

		)VALUES (
			SEQ_CPN_MEM_IDX.NEXTVAL, #{CPN_MST_IDX}, #{MEM_MST_MEM_ID} ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'), NULL, 'N'

			, #{CPN_MEM_ISU_CNL}

		)
