mapper.select:
/* AuthManager.selectAuthManagerList */ SELECT S2.* FROM ( SELECT ROWNUM AS RNUM, S1.* FROM ( SELECT AUT_MST_IDX, AUT_MST_NM, AUT_MST_STATE, AUT_MST_USE_YN, AUT_MST_REG_DT, AUT_MST_REG_IP, AUT_MST_REG_ID, AUT_MST_UPD_DT, AUT_MST_UPD_IP, AUT_MST_UPD_ID FROM T_AUT_MST WHERE 1=1 AND AUT_MST_USE_YN = 'Y'
ORDER BY AUT_MST_REG_DT DESC ) S1 ) S2
WHERE RNUM BETWEEN #{startNum} AND #{endNum}

mapper.insert:
/* AuthManager.insertAuthManager */ INSERT ALL
INTO T_ADM_AUT ( ADM_AUT_MST_IDX, ADM_ADM_MNU_IDX ) VALUES ( #{AUT_MST_IDX}, #{ADM_MNU_IDX} )
SELECT * FROM DUAL
